import React, { useState } from 'react';
import './SearchBar.css'


const SearchBar = ({ onSearch }) => {
  const [searchTerm, setSearchTerm] = useState('');

  const handleInputChange = (event) => {
    setSearchTerm(event.target.value);
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    onSearch(searchTerm);
  };

  return (
    <div id="app-cover">
  <div id="app">
    <form method="get" action="" onSubmit={handleSubmit} >
      <div id="f-element">
        <div id="inp-cover"><input type="text" name="query" placeholder="Type something to search ..." autocomplete="off" value={searchTerm} onChange={handleInputChange}/></div>
      </div>
      <button type="submit" class="shadow"><i class="fas fa-search"></i></button>
    </form>
  </div>
  <div id="layer" title="Click the blue area to hide the form"></div>
  <div id="init"></div>
</div>
  );
};

export default SearchBar;
